jsxgraph.input:
var board = JXG.JSXGraph.initBoard('jsxgraph-integral-curves', {axis: true, boundingbox: [-12, 10, 12, -10]});
var t0 = board.create('slider', [[-11, 8], [-5, 8], [-12, -3, 12]], {name:'t0'});
var y0 = board.create('slider', [[-11, 6], [-5, 6], [-10, 2.4, 10]], {name:'y0'});
var y = board.create(
  'curve', [
  function(t) {return t;},
  function(t) {
    return y0.Value()*Math.exp(-0.3*t0.Value())*Math.exp(0.3*t);
  },
  -12,
  12],
  {
    strokewidth:3,
  }
);
var lab1 = board.create('text',
  [-10, 3, function() {
    return "dy/dt = 0.3y";
    }]
);
var lab2 = board.create('text',
  [-10, 1.5, function() {
    return "y(" + t0.Value().toFixed(2) + ") = " + y0.Value().toFixed(2);
  }]
);
var p = board.create('point', [
  function() {return t0.Value();},
  function() {return y0.Value();},
], {name:"(t0, y0)", size:2}
);
var xslice = board.create('line',[p, function() {return [p.X(), 0];}], {strokeColor:'#cccccc', strokewidth:1});
var yslice = board.create('line',[p, function() {return [0, p.Y()];}], {strokeColor:'#cccccc', strokewidth:1});
